const filterValues = Inputs.FilterValues||{};
Component.Object.FilterValues = filterValues;

// Create the filter items array (all form controls)
Component.Object.FilterItems = (Inputs.Filters||[]).map(c => {

    const filterItem = {
        id:Component.Object.id + '-' + c.Name,
        Value:filterValues!==undefined?filterValues[c.Name]:undefined,
        Name:c.Name,
    }

    // Copy non function values to the new object
    for(let key in c) { 
        if(typeof c[key] !== "function" && typeof c[key] !== "undefined")
            filterItem[key] = c[key]
    }

    return Noodl.Object.create(filterItem)
})

// Function to update current filter
Component.UpdateFilter = function() {
    const filterItems = Component.Object.FilterItems.filter(fi => fi.Condition!==undefined && fi.Condition!==null);
    const filters = filterItems.map(fi => fi.Condition);

    let filter;
    if(filters.length === 0) filter = {};
    else if(filters.length === 1) filter = filters[0]
    else filter = {
        and:filters
    }

    if(JSON.stringify(Component.Object.Filter) !== JSON.stringify(filter)) {
        // Filter has changed
        Component.Object.Filter = filter;
        Component.ScheduleChanged();
    }
}

// Schedule a change output
Component.Object._ChangeScheduled = false;
Component.ScheduleChanged = function() {
    if(Component.Object._ChangeScheduled) return;
    Component.Object._ChangeScheduled = true;
    setTimeout(() => {
        Component.Object._ChangeScheduled = false;
        Outputs.FiltersChanged();
    },10);
}

// Listed to condition changed event
Noodl.Events.on(Component.Object.id + '-Filters.Changed',(ev) => {
    // Only update if value changed
    if(Component.Object.FilterValues[ev.Name] !== ev.Value) {
        const filterValue = {};
        filterValue[ev.Name] = ev.Value;
        Component.Object.FilterValues = Object.assign({},Component.Object.FilterValues,filterValue);
         Noodl.Objects[Component.Object.id + '-' + ev.Name].Value = ev.Value;

        Outputs.FilterValuesChanged();
    }

    Component.UpdateFilter();  
})

// Wait for all filter controls to create before updating
// filter
setTimeout(() => {
    Component.UpdateFilter();
},10)

const filterValues = {};
Component.Object.FilterItems.forEach(f => {
    filterValues[f.Name] = null;
    f.Value = null;
})

Component.Object.FilterValues = filterValues;
Outputs.FilterValuesChanged();

// Let filter controls update conditions
setTimeout(() => {
    Component.UpdateFilter();
},10)

// Update the value and the condition
Component.Object.Value = Inputs.Value;
Component.UpdateCondition();

const filterItem = Component.RepeaterObject;

// Emit an event that the condition has been updated
Noodl.Events.emit(filterItem.id.split('-')[0] + '-Filters.Changed',{
    Value:Component.Object.Value,
    Name:filterItem.Name,
    Condition:filterItem.Condition,
})

Component.Object.Value = !!Inputs.Value;

Component.UpdateCondition = function() {
    const obj = Component.RepeaterObject;
    const value = Component.Object.Value;

    if(value) { // A checked value means make the test
        const cond = {};

        cond[Inputs.Property] = Inputs.Test;

        obj.Condition = cond;
    }
    else obj.Condition = null; // No checked means ignore the test
}

Component.UpdateCondition();

