const TextInputBlurred = () => {
    Component.Object.TextInputIsFocused = false

    const textInput = Script.Inputs.TextInput.getDOMElement().querySelectorAll('input')[0]
    const dateString = textInput.value

    const date = parseDate(dateString)
    Component.Object.Date = date
    Script.Outputs.DateSelected()

    document.body.removeChild(datePopup)
}

const DateStringChanged = (e) => {
    const dateString = e.target.value

    if(Component.Object.TextInputIsFocused) {
        const date = parseDate(dateString)

        // The date have changed in the component object, update the
        // date picker
        if(datepicker !== undefined) {
            datepicker.setDate(date)
        }

        if(dateInput !== undefined) {
            dateInput.value = date.getFullYear() + '-' + date.getMonth() + '-' + date.getDate()
        }
    }
}

