const base_url = Inputs.api

const id = Inputs.id

if (id && base_url) {
    Outputs.api_url = `${base_url}/${id}?populate=role`
}

const Ip1 = Inputs.Ip1;
const Ip2 = Inputs.Ip2;
const Ip3 = Inputs.Ip3;
const Ip4 = Inputs.Ip4;
const Ip5 = Inputs.Ip5;

const error = Inputs.error

const isFormValid = Ip1 && Ip2 && Ip3 && Ip4 && Ip5;

if (error) {
    Outputs.result = false
    return
}
Outputs.result = isFormValid

const Tinh = Inputs.Tinh
const Huyen = Inputs.Huyen
const Xa = Inputs.Xa

let HienHuyen = false
let HienXa = false

if(Tinh) {
    HienHuyen = true
}

if(Huyen) {
    HienXa = true
}

Outputs.HienHuyen = HienHuyen
Outputs.HienXa = HienXa

const numPages = Math.ceil(Inputs.TotalCount / Inputs.PageSize);
const selectedPage = Inputs.SelectedPage;

// The number of direct pages that first in the control
const _width = Inputs.Width - 214; // Remove margin, buttons etc
if(isNaN(_width)) return;

const numPageSlots = Math.min(Math.floor(_width / 45),numPages);


if(numPages > 1) {
    Outputs.LastPageIndex = numPages-1;
    Outputs.LastPageVisible = true;
}
else
    Outputs.LastPageVisible = false;


const pages = [];

if(numPageSlots >= (numPages-2)) {
    // All pages fit
    for(let p = 1; p < numPages-1; p++) pages.push({
        id:Component.Object.id + '-' + p,
        Index:p
    })

    Outputs.LastEllipsis = false;
    Outputs.FirstEllipsis = false;
}
else {
    let firstIndex = selectedPage;
    let lastIndex = selectedPage;
    while(lastIndex - firstIndex < (numPageSlots-1)) {
        firstIndex = Math.max(1,firstIndex-1);
        lastIndex = Math.min(numPages-2,lastIndex+1);
    }

    let lastEllipsis = false;
    if(lastIndex < numPages-2) { 
        // We did not reach the end of num pages, make room for ...
        lastIndex--;
        lastEllipsis = true;
    }

    let firstEllipsis = false;
    if(firstIndex > 1) { 
        // We did not reach the end of num pages, make room for ...
        firstIndex++;
        firstEllipsis = true;
    }

    for(let p = firstIndex; p <= lastIndex; p++) pages.push({
        id:Component.Object.id + '-' + p,
        Index:p
    })

    Outputs.LastEllipsis = lastEllipsis;
    Outputs.FirstEllipsis = firstEllipsis;
}

Component.Object.NumPages = numPages;
Component.Object.Skip = Inputs.SelectedPage * Inputs.PageSize;

Outputs.BackEnabled = Inputs.SelectedPage > 0;
Outputs.NextEnabled = Inputs.SelectedPage < numPages-1;

Outputs.Pages = pages;

if(Component.Object['Selected Page'] > 0)
    Component.Object['Selected Page']--;

Outputs.Changed();

if(Component.Object['Selected Page'] < Component.Object.NumPages-1)
    Component.Object['Selected Page']++;

Outputs.Changed();

if(Inputs.Selected) {
    Outputs.BgColor = "Primary";
    Outputs.TextColor = "White";
}
else if(Inputs.Hover) {
    Outputs.BgColor = "Grey - 300";
    Outputs.TextColor = "Grey - 900";
}
else {
    Outputs.BgColor = "transparent";
    Outputs.TextColor = "Grey - 900";
}

const cellWidth = '1%'

if(Inputs.Headers) {
    // Use headers specified as input
    Outputs.HeaderItems = Inputs.Headers.map(h => ({
        id:Component.Object.id + '-' + h.Field,
        Label:h.Label,
        Width:h.Width||cellWidth,
        Editable:!!h.Editable,
        Field:h.Field,
        Type:h.Type,
        Sortable:!!h.Sortable
    }))
}
else {
    // Use keys from items input
    // Extract all keys from item properties
    const allKeys = {}
    Inputs.Items.forEach(i => {
        for(let key in i) {
            if(typeof i[key] === 'function' || typeof i[key] === 'undefined') continue;
            allKeys[key] = true
        }

    })

    const headerKeys = Object.keys(allKeys)
     Outputs.HeaderItems = headerKeys.map(h => ({
        id:Component.Object.id + '-' + h,
        Label:h,
        Field:h,
        Width:cellWidth
    }))
}

Outputs.RowItems = Inputs.Items.map(i => ({
    id:i.id?(Component.Object.id + '-row-' + i.id):undefined, // Component unique ids to avoid refreshing entire table
    ItemId:i.id,
    Columns:Outputs.HeaderItems.map(h => ({
        id:i.id?(Component.Object.id + '-' + h.Field +  '-' + i.id):undefined,
        Type:h.Type,
        Editable:h.Editable,
        Field:h.Field,
        ItemId:i.id,
        Width:h.Width || cellWidth,
        Value:i[h.Field]
    }))
}))

Noodl.Events.on(Component.Object.id + '-Table.CellChanged',(ev) => {
    Outputs.ItemId = ev.ItemId
    Outputs.Field = ev.Field
    Outputs.Changed()
})

