// Format a date object into a string
function formatDate(date) {
    if(!(date instanceof Date)) return

    const months = [
        'Jan',
        'Feb',
        'Mar',
        'Apr',
        'May',
        'Jun',
        'Jul',
        'Aug',
        'Sep',
        'Oct',
        'Nov',
        'Dec'
    ]

    let newDateString = months[date.getMonth()] + ' ' +date.getDate() + ', ' + date.getFullYear()

    return newDateString
}

// Parse a date string in the format we use into a date object
// if possible
function parseDate(_dateString) {
    // No valid date, return todays date
    if(_dateString === undefined || _dateString.length < 3) 
        return null

    let dateString = _dateString.trim().toLowerCase()

    const months = {
        'jan':0,
        'feb':1,
        'mar':2,
        'apr':3,
        'may':4,
        'jun':5,
        'jul':6,
        'aug':7,
        'sep':8,
        'oct':9,
        'nov':10,
        'dec':11
    }

    let month = months[dateString.substring(0,3)]
    if(month === undefined) return new Date() // Not a valid month

    // Remove month
    dateString = dateString.substring(3)
    dateString = dateString.trim()

    let sepIndex = dateString.indexOf(',')
    let dayString = dateString.substring(0,sepIndex==-1?dateString.length:sepIndex)

    let day = parseInt(dayString) || 1

    let year = (new Date()).getFullYear()
    if(sepIndex !== -1) {
        dateString = dateString.substring(sepIndex+1)
        dateString = dateString.trim()

        if(dateString.length === 4)
        year = parseInt(dateString)
    }

    return new Date(year,month,day)
}

// Compare two dates, ignoring the time
function compareDates(a,b) {
    if(a.getFullYear() === b.getFullYear() && a.getDate() === b.getDate() && a.getMonth() === b.getMonth())
        return true

    return false
}

var dateInput,datepicker,datePopup

const TextInputFocused = () => {
    Component.Object.TextInputIsFocused = true

    // If the text input is focused and we are not running on a mobile device
    // launch the popup
    if(!isMobile()) {
        const textInput = Script.Inputs.TextInput.getDOMElement().querySelectorAll('input')[0]
        const rect = textInput.getBoundingClientRect()

      //  let top = rect.bottom + Script.Inputs.PopupMarginTop
      //  let left = rect.left + Script.Inputs.PopupMarginRight

        datePopup = document.createElement('div')
        datePopup.style.zIndex = 100
        datePopup.style.position = 'absolute'
        datePopup.style.border = "1px solid " + Script.Inputs.BorderColor
        datePopup.style.borderRadius = "4px"
        datePopup.style.top = (rect.bottom + Script.Inputs.PopupMarginTop) + 'px'
        datePopup.style.left = (rect.left + Script.Inputs.PopupMarginRight) + 'px'
        document.body.appendChild(datePopup)

        // This is running on desktop, install the vanilla js
        injectDatePicker(() => {
            // Script is loaded

            datepicker = new Datepicker(datePopup, {

            }); 

            // adjuste position if outside of screen
            const rect = datePopup.getBoundingClientRect()
            if(rect.top + rect.height > window.innerHeight) {
                datePopup.style.top = Math.max(0,window.innerHeight - rect.height) + 'px'
            }
            if(rect.left + rect.width > window.innerWidth) {
                datePopup.style.left = Math.max(0,window.innerWidth - rect.width) + 'px'
            }



            if(Component.Object.Date !== undefined) {
                datepicker.setDate(Component.Object.Date)
            }

            datePopup.addEventListener('changeDate',() => {
                // The picker reports a changed date

            })

            datePopup.addEventListener('click',() => {
                // The user have clicked a date in the popup
                var date = datepicker.getDate()

                // If the date is a new date
                if(Component.Object.Date === undefined || !compareDates(Component.Object.Date,date)) {

                    // Blur the text input so the date can be updated
                    Component.Object.Date = date
                    textInput.blur()
                }
            })

        })

    }
}

const TextInputBlurred = () => {
    Component.Object.TextInputIsFocused = false

    const textInput = Script.Inputs.TextInput.getDOMElement().querySelectorAll('input')[0]
    const dateString = textInput.value

    const date = parseDate(dateString)
    Component.Object.Date = date
    Script.Outputs.DateSelected()

    document.body.removeChild(datePopup)
}

const DateStringChanged = (e) => {
    const dateString = e.target.value

    if(Component.Object.TextInputIsFocused) {
        const date = parseDate(dateString)

        // The date have changed in the component object, update the
        // date picker
        if(datepicker !== undefined) {
            datepicker.setDate(date)
        }

        if(dateInput !== undefined) {
            dateInput.value = date.getFullYear() + '-' + date.getMonth() + '-' + date.getDate()
        }
    }
}

Script.Inputs = {
    PopupMarginRight: 'number',
    PopupMarginTop: 'number',
    Text: 'color',
    TextMuted: 'color',
    Accent: 'color',
    Background: 'color',
    SelectedDateBackground: 'color',
    SelectedDateText: 'color',
    HoverDateBackground: 'color',
    HoverDateText: 'color'
}

Script.Signals.DidMount = () => {
    Script.Outputs.IsMobile = isMobile()

    injectDatePickerStyles()

    // Register blur / focus listeners
    if(Script.Inputs.TextInput.getDOMElement() !== undefined) {
        var textInput = Script.Inputs.TextInput.getDOMElement().querySelectorAll('input')[0]
        textInput.addEventListener('focus',TextInputFocused)
        textInput.addEventListener('blur',TextInputBlurred)
        textInput.addEventListener('keydown',(e) => {
            if(e.keyCode===13) textInput.blur()
        })
        textInput.addEventListener('keyup',DateStringChanged)
    }

    // If there is a date set from start, format it 
    if(Component.Object.Date !== undefined) {
        let formattedDate = formatDate(Component.Object.Date)
        Component.Object['Formatted Date'] = formattedDate
    }


    Component.Object.on('change',(ev) => {
        if(ev.name === 'Date') {
            const date = Component.Object.Date
            // The date have changed in the component object, update the
            // date picker
            if(datepicker !== undefined) {
                datepicker.setDate(date)
            }

            if(dateInput !== undefined) {
                dateInput.value = date.getFullYear() + '-' + date.getMonth() + '-' + date.getDate()
            }

            let formattedDate = formatDate(date)
            if(textInput) textInput.value = formattedDate
            Component.Object['Formatted Date'] = formattedDate
        }
    })

    if(isMobile()) {
        // On Mobile create a native date input hidden
        // under the icon, clicking it will show the
        // native picker
        const elem = Script.Inputs.TextInput.getDOMElement()

        dateInput = document.createElement('input')
        dateInput.className = 'ndl-datepicker-native-input'

        elem.parentNode.appendChild(dateInput)

        dateInput.type = "date"

        dateInput.onchange = () => {
            var date = new Date(dateInput.value)
            Component.Object.Date = date
            Script.Outputs.DateSelected()
        }
    }
}

