if(Inputs.ThisField === Inputs.Sorting) {
    Outputs.State = 'Asc';
}
else if(("-"+Inputs.ThisField) === Inputs.Sorting) {
     Outputs.State = 'Desc';
}
else {
    Outputs.State = 'None';
}

if(Component.RepeaterObject.Sortable) {
    if(Component.ParentObject.Sorting === Component.RepeaterObject.Field) {
        Component.ParentObject.Sorting = '-' + Component.RepeaterObject.Field;
        Noodl.Events.emit(Component.ParentObject.id + '-SortingChanged');
    }
    else {
        Component.ParentObject.Sorting = Component.RepeaterObject.Field;
        Outputs.SortingChanged();
        Noodl.Events.emit(Component.ParentObject.id + '-SortingChanged');
    }
}

