const res = Inputs.Response;

if (!res) {
    Outputs.Failure();
    return;
}

const sosRequests = res.map(item => item.SOSRequest);

const allEmpty = sosRequests.every(request => 
    !request || 
    Object.keys(request).length === 0 || 
    (Object.keys(request).length === 1 && request.hasOwnProperty('id'))
);

if (allEmpty) {
    Outputs.HasData = false;
    return;
}

Outputs.HasData = true;

const jsonString = Inputs.JsonString;

if (!jsonString) return;

try {
    const parsedData = JSON.parse(jsonString);
    if (Array.isArray(parsedData) && parsedData.length > 0) {
        Outputs.Id = parsedData[0].id;
        Outputs.Success();
    } else {
        Outputs.Error();
    }
} catch (error) {
    Outputs.Error();
}

